it("should remove summary suffix", () => {
  const getOne = vi.fn();
  const getList = vi.fn();
  const getMany = vi.fn();
  const getManyReference = vi.fn();
  const create = vi.fn();
  const del = vi.fn();
  const deleteMany = vi.fn();
  const update = vi.fn();
  const updateMany = vi.fn();

  const dataProvider: DataProvider = withSupabaseFilterAdapter({
    getOne,
    getList,
    getMany,
    getManyReference,
    create,
    delete: del,
    deleteMany,
    update,
    updateMany,
  });

  expect(
    Promise.all([
      dataProvider.getOne("resource_summary", { id: 1 }),
      dataProvider.getList("resource_summary", {
        pagination: { page: 1, perPage: 10 },
      }),
      dataProvider.getMany("resource_summary", { ids: [1] }),
      dataProvider.getManyReference("resource_summary", {
        id: 1,
        target: "target",
        pagination: { page: 1, perPage: 10 },
        sort: { field: "id", order: "ASC" },
        filter: {},
      }),
      dataProvider.create("resource_summary", { data: {} }),
      dataProvider.delete("resource_summary", { id: 1 }),
      dataProvider.deleteMany("resource_summary", { ids: [1] }),
      dataProvider.update("resource_summary", {
        id: 1,
        data: {},
        previousData: {},
      }),
      dataProvider.updateMany("resource_summary", { ids: [1], data: {} }),
    ]),
  ).resolves.toHaveLength(9);

  expect(getOne).toHaveBeenCalledWith("resource", { id: 1 });
  expect(getList).toHaveBeenCalledWith("resource", {
    pagination: { page: 1, perPage: 10 },
    filter: undefined,
  });
  expect(getMany).toHaveBeenCalledWith("resource", { ids: [1] });
  expect(getManyReference).toHaveBeenCalledWith("resource", {
    id: 1,
    target: "target",
    pagination: { page: 1, perPage: 10 },
    sort: { field: "id", order: "ASC" },
    filter: {},
  });
  expect(create).toHaveBeenCalledWith("resource", { data: {} });
  expect(del).toHaveBeenCalledWith("resource", { id: 1 });
  expect(deleteMany).toHaveBeenCalledWith("resource", { ids: [1] });
  expect(update).toHaveBeenCalledWith("resource", {
    id: 1,
    data: {},
    previousData: {},
  });
  expect(updateMany).toHaveBeenCalledWith("resource", { ids: [1], data: {} });
});

export const UNQUOTED_ALLOWED_CHARS = "[A-Za-zÀ-ÖØ-öø-ÿ0-9-]+";
export const QUOTED_ALLOWED_CHARS = "[A-Za-zÀ-ÖØ-öø-ÿ0-9, -]+";
export const LIST_REGEX_BASE = `(${UNQUOTED_ALLOWED_CHARS}|"${QUOTED_ALLOWED_CHARS}")(,(${UNQUOTED_ALLOWED_CHARS}|"${QUOTED_ALLOWED_CHARS}"))*`;

/**
 * List represents a list of values, quoted or not.
 *
 * e.g. 1
 * e.g 1,2
 * e.g "a","b"
 * e.g "a",b
 */
export function parseList(list: string) {
  const parsedItems = [];

  let currentItem = "";
  let currentQuoted = false;
  for (const char of list) {
    if (char === ",") {
      if (currentQuoted) {
        currentItem += char;
        continue;
      }

      parsedItems.push(currentItem);
      currentItem = "";
      continue;
    }

    if (char === '"') {
      currentQuoted = !currentQuoted;
      continue;
    }

    currentItem += char;
  }
  if (currentItem) {
    parsedItems.push(currentItem);
  }

  return parsedItems.map((v: string) => {
    const parsedFloat = Number.parseFloat(v);
    if (!Number.isNaN(parsedFloat)) {
      return parsedFloat;
    }
    return v;
  });
}

import faker from "faker/locale/en";

export const weightedArrayElement = (values: any[], weights: any) =>
  faker.random.arrayElement(
    values.reduce(
      (acc, value, index) => acc.concat(new Array(weights[index]).fill(value)),
      [],
    ),
  );

export const weightedBoolean = (likelyhood: number) =>
  faker.datatype.number(99) < likelyhood;

export const randomDate = (minDate?: Date, maxDate?: Date) => {
  const minTs =
    minDate instanceof Date
      ? minDate.getTime()
      : Date.now() - 5 * 365 * 24 * 60 * 60 * 1000; // 5 years
  const maxTs = maxDate instanceof Date ? maxDate.getTime() : Date.now();
  const range = maxTs - minTs;
  const randomRange = faker.datatype.number({ max: range });
  // move it more towards today to account for traffic increase
  const ts = Math.sqrt(randomRange / range) * range;
  return new Date(minTs + ts);
};

export const randomFloat = (min: number, max: number) =>
  parseFloat(faker.datatype.number({ min, max, precision: 0.01 }).toFixed(2));

