        fields: props?.fields,
        insert: props?.insert,
        move: props?.move,
        prepend: props?.prepend,
        remove: props?.remove,
        replace: props?.replace,
        swap: props?.swap,
        update: props?.update,
      }) as ArrayInputContextValue,
    [props],
  );

  if (props?.fields) {
    return memo;
  }
  if (!context) {
    throw new Error(
      "useArrayInput must be used inside an ArrayInputContextProvider",
    );
  }

  return context;
};
